css:
footer ul {
    display: flex;
    justify-content: space-between;
    padding: 0;
}

footer ul li {
    list-style: none;
}

footer {
    position: fixed;
    bottom: 0 !important;
    width: 100%;
    height: 35px;
}

.delivery-table, th, td {
    border: 1px solid black;
    font-family: none;
    border-collapse: collapse;
    color: black !important;
}

.h-desc {
    width: 143px;
}

.d-note {
    text-align: center;
    font-family: none;
    font-size: 20px;
    font-weight: 600;
}

.delivery-table th {
    background-color: #55c1af;
    text-align: center;
    width: 5px;
}

.delivery-table td {
    text-align: center;
}

.special-td {
    text-align: left !important;
}

.center {
    top: 50px;
}

.h-title {
    font-weight: 600;
}

.print-format p {
    margin: 0px;
}

header {
    display: flex !important;
    width: 100%;
    justify-content: space-between !important;
    background: white; 

}

.reciever {
    line-height: 2;
    font-family: none;
    margin-top: 20px;
}

.r-title {
    font-weight: bold !important;
}

.header-table {
    display: flex;
    gap: 10px;
    font-family: none;
}

.header-table table {
    width: 50%;
}

.invoice-table tr td {
    line-height: normal !important;
    padding: 0 !important;
}

.table-bordered {
    width: 100%;
    border-color: #000 !important;
}

.table-bordered th {
    padding: 3px !important;
    font-size: 11px;
    text-align: center;
    font-weight: bold;
    color: #000;
    border-color: #000 !important;
}

.table-bordered td {
    border-color: #000 !important;
    padding: 0 !important;
}

.rate span:nth-child(2) {
    margin-left: auto;
}

.b-none {
    border: none;
    padding: 0px !important;
    border-top: none !important;
}

@media print {
    .main-table {
        width: 100%;
        border-collapse: collapse;
    }

    .main-table-head {
        display: table-header-group !important;

    }

    .main-table-foot {
        display: table-footer-group !important;
    }

    .main-table-body {
        display: table-row-group !important;
    }




    .main-table td main {
        page-break-inside: auto !important;
    }


}
