css:
table{
    width: 100%;
}

header{
    display: flex !important;
}

.border{
    border: 1px solid #000;
}

header .logo{
    margin-left: auto;
}

header .logo img{
    width: 100%;
}

.table-borderless tr,.table-borderless td,.table-borderless th{
    border: none !important;
}

.header-table{
    display: flex ;
    width: 100%;
}

.header-table table{
    width: 50%;
}

.invoice-table tr td{
    line-height: normal !important;
    padding: 2px 0px  !important;
}

.auth-table tr td{
    padding: 2px 0 !important;
    line-height: normal !important;
}


.table-bordered{
    width: 100%;
    border-color: #000 !important;
}


.table-bordered th{
    padding: 3px !important;
    font-size: 11px;
    text-align: center;
    font-weight: bold;
    color: #000;
    border-color: #000 !important;
}
.table-bordered td{
    border-color: #000 !important;
       padding: 0 !important; 
}

.rate span:nth-child(2){
    margin-left: auto;
}

.print-format td, .print-format th{
    padding: 0px 2px !important;
}

.delivery-table th{
    background-color: #55c1af;
    text-align:center;
    width:5px;
    font-size: 12px;
}
.delivery-table td{
    text-align:center;
}
.special-td{;
    text-align: left !important;
}


.delivery-table td{
    font-size: 15px !important;
}

.delivery-table th,.delivery-table tr.border-row td {
  border: 1px solid black;
  border-collapse: collapse;
  color:black !important;
}

.delivery-table td span:nth-child(2),.footer-table td span:nth-child(2){
    float: right;
}

.border-none{
    border: none !important;
}

.footer-table tbody{
    width: 100%;
    display: flex !important;
    justify-content: end;
}

.mt-3{
    margin-top: 15px !important;
    width: 250px;
}


.mt-3 td{
    padding: 5px 2px !important;
}

footer ul {
    display:flex;
    justify-content:space-between;
    padding:0;
}
footer ul li{
    list-style:none;
}
footer{
    position:fixed;
    bottom:0 !important;
    width:100%;
    height:35px;
}
